mapper.insert:
INSERT INTO MEMBER (	
						MEMBER_ID				
						, PASSWORD			
						, NAME				
						, SALT
						, GENDER
						, BIRTH
						, EMAIL

					) 
VALUES 				(
						#{memberId}				
						, #{password}			
						, #{name}				
						, #{salt}
						, #{gender}
						, #{birth}
						, #{email}
					)

mapper.select:
SELECT	count(1)
FROM	MEMBER
WHERE	MEMBER_ID = #{memberId}

SELECT	SALT 
FROM	MEMBER
WHERE	MEMBER_ID = #{memberId}

mapper.update:
UPDATE	MEMBER
SET		POINT  = POINT + #{point}
WHERE	MEMBER_ID = #{memberId}

UPDATE	MEMBER
SET		LOGIN_FAIL_COUNT = 0 
WHERE	MEMBER_ID = #{memberId}
